	   function addUserFormValidate(){

		   
		   addUserForm.submit(function(e){
				//e.preventDefault();
			   return false;
			}).validate({
				ignore: [],
				rules: {
					username : {
						 required: true
					}, 
					email : {
						required: true,
						email: true
					},
					password: {
						required: true,
						minlength: 4
					},
					oneBugFullName: {
						required: true,
					}
				}, 
				 submitHandler: function(form) {
					 //addUserOkBtn.button('loading');
						$.ajax({
							type: addUserForm.attr('method'),
							url: addUserForm.attr('action'),
							data: addUserForm.serialize(),
							cache : false,
							success: function (data) {
								var dataObj = data;
								var id= dataObj.id;
								var rowArray=addUserForm.serializeArray();
								var userName=rowArray[0].value;
								var password=rowArray[1].value;
								var oneBugName=rowArray[2].value;
								var email=rowArray[3].value;
								//encrpted password
								var encryptedPassword = $.sha1(password);
								//encryptedPassword=encryptedPassword.toUppercase();
								userInfoDataTable.fnAddData([id,userName,encryptedPassword,oneBugName,email]);
								userTableMakeEditable();
								$("#addUserModal").modal('hide');
								alertify.log(dataObj.message,"success");
							},
							error : function(XMLHttpRequest, textStatus, errorThrown) {
								alertify.log("submit user info failed","error");
							},
							
							complete: function (XMLHttpRequest, textStatus) {
								//addUserOkBtn.button('reset');
							}
						});	
					return false;
				},
				highlight: function(element) {
					$(element).closest('.form-group').removeClass('has-success').addClass('has-error');
				},
				success: function(element) {
					element
					.text('OK!').addClass('valid')
					.closest('.form-group').removeClass('has-error').addClass('has-success');
				},

			});
	   }
	   
